#include <iostream>
using namespace std;
const M=100005;
int n,last[M],cnt,size[M],d[M],ans=999999999;
struct Node 
{
	int to;
	int nxt;
}e[M];

void pre(int x,int y) //邻接表存图预处理~
{
	cnt++;
	e[cnt].to=y;
	e[cnt].nxt=last[x];
	last[x]=cnt;
}

void dfs(int x,int fa)
{
	for(int i=last[x];i;i=e[i].nxt)
	{
		int y=e[i].to;
		if(y!=fa)//子树当然不包括爹地咯
		{
			size[x]++;
			dfs(y,x);
			size[x]+=size[y];
		}
		else continue;
	}
}/*这里的size相当于把son1和son2合并了，这里求的是
son1，因为son1只用来求dp[1]，所以出去之后全部+1就
可以*/

void dfs1(int x,int y)
{
	for(int i=last[x];i;i=e[i].nxt)
	{
		if(x!=1)
			d[x]=d[y]+n-2*size[x];
		if(e[i].to==y)
			continue;
		dfs1(e[i].to,x);
	}
}

int main()
{
	cin>>n;
	for(int i=1;i<n;i++)
	{
		int a,b;
		cin>>a>>b;
		pre(a,b);
		pre(b,a);//要存无向图~
	}

	dfs(1,0);//这里是为了求注释里的son1~
	for(int i=1;i<=n;i++)
		d[1]+=size[i];//这里的d数组就相当于dp

	for(int i=1;i<=n;i++)
		size[i]+=1;

	dfs1(1,0);//开始求总路程

	for(int i=1;i<=n;i++)
		if(d[i]<ans)
		{
			ans=d[i];
			cnt=i;
		}
	cout<<cnt<<" "<<ans;
	return 0;
}
