```cpp

#include <cstdio>
#include <algorithm>
#define ll long long
using namespace std;
const int a[6] = {0, 1, 3, 5, 8, 10};
ll f(int m)
{
    if(m <= 5) return a[m];
    //n太小的话还是直接输出的好
    ll sum = 0;
    int i;
    for(i = 1; i * i <= m; ++i)
