    int k = minz[x];
    int an = 1;
    int cnt = 0;
    while(x > 1)
    {
        if(k != minz[x])
        {
            an *= (cnt + 1);
            k = minz[x];
            cnt = 0;
        }
        x /= minz[x];
        cnt ++;
    }
     return an * (cnt + 1);
}
