		l=min(l,a[i]);
		r=max(r,a[i]);
	}
	for(int i=1;i<=500;i++){//直接while(r-l>=1e-6)过不了！毒瘤
		double mid=(l+r)/2;
		if(check(mid)){
			ans=mid;
			l=mid;
		}
		else r=mid;
	}
	printf("%.0lf",floor(ans/10));//这里不加floor就过不了最后一个点
	return 0;
}
