#  sol:
# 二分答案，从0到2000中二分一个平均值，对于原先每个数减去平均值，顺便做一下前缀和，再寻找一下是否有一段区间的值大于等于0就可了

```cpp
#include <cstdio>
#include <algorithm>
using namespace std;
#define eps 1e-5
int n, m;
double a[100005], b[100005], sum[100005];
inline bool check(double mid)
{
	for(int i = 1; i <= n; i++)
	b[i] = (double)(a[i] - mid),
	sum[i] = (double)sum[i - 1] + b[i];
	double mi = 9000000000000;
	for(int i = m; i <= n; i++)
	{
		mi = min(mi, sum[i - m]);
		if ((double)(sum[i] - mi) + eps > 0)
		return true;
	}
	return false;
}
int main()
{
	scanf("%d%d", &n, &m);
	for(int i = 1; i <= n; i++)
	scanf("%lf", &a[i]);
	sum[0] = 0;
	double l = 0, r = 2000.00;
	for(int i = 1; i <= 100; i++)
	{
		double mid = (l + r) / 2.00;
		if (check(mid)) l = mid;
		else r = mid;
	}
	printf("%d\n",(int)(l * 1000));
}
```
