```cpp
#include <bits/stdc++.h>
using namespace std;
const int N=20;
int n,a[N],ans[N][N],vst[N],sum;
bool isans(){//判断是否为正确的矩阵
	int cnt=0;
	//横向
	for(int i=1;i<=n;i++){
		cnt=0;
		for(int j=1;j<=n;j++) cnt+=ans[i][j];
		if(cnt!=sum) return 0; 
	}
	//竖向
	for(int j=1;j<=n;j++){
		cnt=0;
		for(int i=1;i<=n;i++) cnt+=ans[i][j];
		if(cnt!=sum) return 0;
	}
	//两个斜对角
	cnt=0;
	for(int i=1;i<=n;i++) cnt+=ans[i][i];	
	if(cnt!=sum) return 0;

	cnt=0;
	for(int i=1;i<=n;i++) cnt+=ans[i][n-i+1];
	if(cnt!=sum) return 0;

	return 1; 
}
void dfs(int x,int y){
	if(y==n+1) y=1,x++;//换行
	if(x==n+1){//结束之后判断
