	} 
	for(int i=1;i<=n*n;i++)//选择填哪个数
		if(!vst[i]){
			vst[i]=1; ans[x][y]=a[i];
			dfs(x,y+1);
			vst[i]=0; ans[x][y]=0;
		}
}
