		que[tl].l = i;
		que[tl++].v = su[i];
		if (su[i + s] - que[hd].v >= 0)
		{
			fl = true;
			break;
		}
	}

	if (!fl)
	{
		for (int i = m; i <= n - s; ++i)
		{
			while (hd < tl && que[hd].l + m <= i)
			{
