		cin>>a[i];  //输入
	for(int k=b;k<=n;k++){
		if(a[k+1]==a[k]+1) ans1++;  //判断是否为连号，如果是+1
		if(ans1>=ans) ans=ans1;  //连号比较，取较大的，再赋值给答案
		if((a[k+1]!=a[k]+1)&&(b<=n)){
		b++;  //如果连号断了，从下一个开始判断
