{
	cin>>s>>x;               //邪恶的猎人埋下了罪恶的探测器。 
	l=s-x;                   //死亡探测器往左边最多扫到的地方。 
	r=s+x;                   //死亡探测器往左边最多扫到的地方。 
	for(;;)                  //坚持不懈的鱼儿向着死亡游去。 
	{
		if (now>r)           //鱼儿的坚持感动了上苍，直接跳过了探测器的扫描范围。 
		{
			cout<<'n';       //活着！！ 
			return 0;        //程序的结束------世界的消逝。 
		}
		if (l<=now&&now<=r)  //被探测器扫到！核弹是否发射？？ 
		{
			if (now+meter>r) //逃出生天！！真小鱼儿从不回头看爆炸！！ 
			{
				cout<<'n';   //活着！！ 
				return 0;    //程序的结束------世界的消逝。 
