#include<cstdio>
#include<cstring>
int i,n;
struct qq 
{
    int value,po;
} a[100001];
int ans[100001];
void merge(int x, int y, int mid)
{
    int p=x,q=mid+1,t=x;
    qq b[100001];
    while (t<=y)
    {
        if (p>mid)
          {
             for (int i=q; i<=y; i++)
               b[t++]=a[q++]; 
             break;
          }
        else if (q>y)
          {
             for (int i=p; i<=mid; i++)
               b[t++]=a[p++]; 
             break; 
          }
        if (a[p].value >= a[q].value) b[t++]=a[p++];
        else
        {
            ans[a[q].po]+=mid-p+1; //核心：此时第二个串中的这个值必定大于串1中未选的数值 
            b[t++]=a[q++];    
        }
    }
    for (int i=x; i<=y; i++)
      a[i]=b[i];
}
void mgsort(qq a[], int x, int y)
{
    if (x>=y) return;
    int mid=(x+y)/2;
    mgsort(a,x,mid);
    mgsort(a,mid+1,y);
    merge(x,y,mid);
}
