          c[x]++;
          x+=lowbit(x);
        }
    }
    int sum(int x) {
        if (x<=0) return 0;
        int ret=0;
        while (x) {
          ret+=c[x];
          x-=lowbit(x);
        }
        return ret;
    }
}t;
