		}
		for(int i=0 ; i<=n+1 ; i++){
			for(int j=0 ; j<=n+1 ; j++){
				s1[i][j] = s2[i][j] = -1e9;  //赋值成负无穷，避免越界
			}
		} 
		for(int d=1 ; d<=n ; d++){   //开始循环
			for(int i=1 ; i+d-1<=n ; i++){
				j = i+d-1;
				s1[i][j] = max(s[i]-f[i+1][j],s1[i+1][j]); //状态转移方程
				s2[i][j] = max(-s[j-1] - f[i][j-1],s2[i][j-1]); //状态转移方程
				f[i][j] = max(s1[i][j]-s[i-1],s[j]+s2[i][j]); //状态转移方程
			}
		}
