	int v1,v2,steps;
	string op; 
};
void print(string op)
{
	cout<<op.length()<<" ";
	for(int i=0;i<op.length();i++)
	{
		switch (op[i])
		{
			case '1':
				cout<<1<<" ";
				//cout<<"fill A"<<endl;
				break;
			case '2':
				cout<<2<<" ";
				//cout<<"fill B"<<endl;
				break;
			case '3':
				cout<<3<<" ";
				//cout<<"empty A"<<endl;
				break;
			case '4':
				cout<<4<<" ";
				//cout<<"empty B"<<endl;
				break;
			case '5':
				cout<<6<<" ";
				//cout<<"pour A B"<<endl;
				break;
			case '6':
				cout<<5<<" ";
				//cout<<"pour B A"<<endl;
				break;				
		}
	}
	cout<<endl;
}
bool bfs()
{
	queue<node>q;
	q.push({0,0,0,""});
	while(!q.empty())
	{
		node cur=q.front();
		int v1=cur.v1,v2=cur.v2,steps=cur.steps;
		string op=cur.op;		
		q.pop();
		//Gang->A
		//A不满 才能倒 倒则倒满 
		if(v1<x && !b[x][v2]) 
		{
			b[x][v2]=1;
			q.push({x,v2,steps+1,op+"1"});
		}
		//Gang->B
		//B不满 才能倒 倒则倒满 
		if(v2<y && !b[v1][y])
		{
			if(y==z)//if(v1==z||y==z)
			{
				op+="2";
				print(op); 
				//cout<<steps+1<<endl;
				return 1;
			}
			b[v1][y]=1;
			q.push({v1,y,steps+1,op+"2"});
		}

		//A->Gang
		//A有水 才能倒 倒则倒空 
		if(v1>0 && !b[0][v2])
		{
			b[0][v2]=1;
			q.push({0,v2,steps+1,op+"3"});
		}
		//B->Gang
		//B有水 才能倒 倒则倒空 
		if(v2>0 && !b[v1][0])
		{
			b[v1][0]=1;
			q.push({v1,0,steps+1,op+"4"});
		}
		//A->B
		//A有水 B不满 才能倒
		if(v1>0 && v2<y)
		{
			//倒空A
			if(y-v2>=v1&&!b[0][v1+v2])
			{
				if(v1+v2==z)
				{
					op+="5";
					print(op);
					//cout<<steps+1<<endl;
					return 1;
				}
				b[0][v1+v2]=1;
				q.push({0,v1+v2,steps+1,op+"5"});
			} 
			//倒满B
			if(y-v2<=v1&&!b[v1-y+v2][y])
			{
				b[v1-y+v2][y]=1;
				q.push({v1-y+v2,y,steps+1,op+"5"});
			}
		}
		//B->A
		//B有水 A不满 才能倒
		if(v2>0 && v1<x)
		{
			//倒空B
			if(x-v1>=v2&&!b[v1+v2][0])
			{
				b[v1+v2][0]=1;
				q.push({v1+v2,0,steps+1,op+"6"});
			} 
			//倒满A
			if(x-v1<=v2&&!b[x][v2-x+v1])
			{
				if(v2-x+v1==z)
				{
					op+="6";
					print(op);
					return 1;
				}
				b[x][v2-x+v1]=1;
				q.push({x,v2-x+v1,steps+1,op+"6"});
			}
		}		
	}
	return 0;
} 
void Init()
{
	memset(b,0,sizeof(b));
}
int main()
{	
	int qaq;
	cin>>qaq;
	for(int o=1;o<=qaq;o++)
	{
		cin>>x>>y>>z;
		Init();
		bfs();
		//if(!bfs())cout<<"impossible"<<endl;
	}

	return 0;
}
