最后放上我的１４９行（ｑｗｑ）代码供大家学习，不要抄题解喔～这是不会进步的行为
```cpp
#include <map>
#include <queue>
#include <bitset>
#include <cstdio>
#include <vector>
#include <cstring>
#include <iostream>
#define maxn 1000
#define ll long long
#define rgi register unsigned int
using namespace std;
inline int read()
{
    int x = 0, f = 1;
    char c = getchar();
    for (; !isdigit(c); c = getchar())
        if (c == '-')
            f = -1;
    for (; isdigit(c); c = getchar())
        x = (x << 3) + (x << 1) + c - '0';
    return x * f;
}
struct Node
{
    int v[2], dis;
    bool operator<(const Node &rhs) const
    {
        return dis > rhs.dis;
    }
};
map<int, int> road;
map<int, int> opt;
int cap[3], ans[maxn], tmp[100000], pos;
bitset<maxn> vis[maxn];
inline void update_ans(int op, int FA, int SON, const Node &u)
{
    int d = u.v[1];
    road[SON] = FA;
    opt[SON] = op;
    ans[d] = u.dis;
}
void BFS(int a, int b, int c)
{
    priority_queue<Node> que;
    cap[0] = a;
    cap[1] = b;
    for (rgi i = 0; i <= maxn; ++i)
        vis[i].reset();
    opt.clear();
    memset(ans, -1, sizeof(ans));
    ans[0] = 0;
    Node start;
    start.dis = 0, start.v[0] = 0, start.v[1] = 0;
    que.push(start);
    vis[0] = 1;
    for (;;)
    {
        //ready
        Node u = que.top();
        que.pop();
        for (rgi i = 0; i <= 1; ++i)
            for (rgi j = 0; j <= 1; ++j)
                if (i != j)
                {
                    //Pour the water at I into J.
                    if (u.v[i] == 0 || u.v[j] == cap[j])
                        continue;                                       //To judge a situation where water cannot be poured.
                    rgi amount = min(cap[j], u.v[i] + u.v[j]) - u.v[j]; //Amount of poured water
                    Node u2 = u;
                    u2.dis++;
                    u2.v[i] -= amount;
                    u2.v[j] += amount;
                    if (!vis[u2.v[0]][u2.v[1]]) //The water of the first two cups is known to know the third Cup.
                    {
                        vis[u2.v[0]][u2.v[1]] = 1;
                        if (i == 0)
                            update_ans(6, u.v[0] * 1000 + u.v[1], u2.v[0] * 1000 + u2.v[1], u2);
                        else
                            update_ans(5, u.v[0] * 1000 + u.v[1], u2.v[0] * 1000 + u2.v[1], u2);
                        if (ans[c] >= 0)
                        {
                            pos = u2.v[0] * 1000 + u2.v[1];
                            return;
                        }
                        que.push(u2);
                    }
                }
        for (rgi i = 0; i <= 1; ++i)
        {
            if (u.v[i] != 0) //be able to pour water
            {
                Node u2 = u;
                u2.v[i] = 0;
                u2.dis++;
                if (!vis[u2.v[0]][u2.v[1]])
                {
                    vis[u2.v[0]][u2.v[1]] = 1;
                    if (i == 0)
                        update_ans(3, u.v[0] * 1000 + u.v[1], u2.v[0] * 1000 + u2.v[1], u2);
                    else
                        update_ans(4, u.v[0] * 1000 + u.v[1], u2.v[0] * 1000 + u2.v[1], u2);
                    if (ans[c] >= 0)
                    {
                        pos = u2.v[0] * 1000 + u2.v[1];
                        return;
                    }
                    que.push(u2);
                }
            }
            if (u.v[i] != cap[i])
            {
                Node u2 = u;
                u2.v[i] = cap[i];
                u2.dis++;
                if (!vis[u2.v[0]][u2.v[1]])
                {
                    vis[u2.v[0]][u2.v[1]] = 1;
                    if (i == 0)
                        update_ans(1, u.v[0] * 1000 + u.v[1], u2.v[0] * 1000 + u2.v[1], u2);
                    else
                        update_ans(2, u.v[0] * 1000 + u.v[1], u2.v[0] * 1000 + u2.v[1], u2);
                    if (ans[c] >= 0)
                    {
                        pos = u2.v[0] * 1000 + u2.v[1];
                        return;
                    }
                    que.push(u2);
                }
            }
        }
    }
}
int main()
{
    int T = read(), a, b, c;
    for (; T; T--)
    {
        a = read(), b = read(), c = read();
        BFS(a, b, c);
        printf("%d ", ans[c]);
        int cnt = 0;
        for (; pos; pos = road[pos])
            tmp[++cnt] = opt[pos];
        for (rgi i = cnt; i >= 1; --i)
            printf("%d ", tmp[i]);
        puts(" ");
    }
    return 0;
}
