```c
#include<cstdio>
#include<algorithm>
#include<stack>
using namespace std;
int c[2],N,f[100010],q[100010][2],ans[100010];
int tim;
int vis[1001][1001];
stack<int>st;
void out(int k) {
	while(f[k]!=0) {
		st.push(k);
		k=f[k];
	}
	printf("%d",st.size());
	while(!st.empty()) {
		k=st.top();
		st.pop();
		printf(" %d",ans[k]);
	}
}
bool p(int k) {
	if(q[k][1]==N) return 1;
	return 0;
}
int p2(int &r) {//判重啊OvO，注意用了地址符'&'，看不懂的小伙伴百度一下
	if(vis[q[r][0]][q[r][1]]==tim) r--;
	else {
		vis[q[r][0]][q[r][1]]=tim;
		if(p(r)) return r;
	}
	return -1;
}
int bfs() {
	int h=1,r=1;
	vis[0][0]=tim;//为什么要用一个递增的量tim，因为vis[][]如果一直标记为1，那第一次循环的结果会影响后面的判重，举个例子：第一次循环时vis[1][1]=1,第二次循环时vis[1][1]的状态明明还没被标记过，但第一次的标记会影响到
	while(h<=r) {
		for(int i=0; i<2; i++) {
			if(q[h][i]!=0) {
				q[++r][i]=0;
				q[r][(i+1)&1]=q[h][(i+1)&1];//假如i=1,则(i+1)&1=0,用于在0,1之间相互转换，下面也是一样OvO
				f[r]=h;
				ans[r]=i+3;//0+3=3,1+3=4,正好对应答案操作的编号OvO
				if(p2(r)==r) return r;
			}
			if(q[h][i]!=c[i]) {
				q[++r][i]=c[i];
				q[r][(i+1)&1]=q[h][(i+1)&1];
				f[r]=h;
				ans[r]=i+1;
				if(p2(r)==r) return r;
			}
			for(int j=0; j<2; j++) {
				if(i==j||q[h][i]==0||q[h][j]==c[j])continue;
				int w=min(q[h][i],c[j]-q[h][j]);
				q[++r][0]=q[h][0];
				q[r][1]=q[h][1];
				f[r]=h;
				q[r][i]-=w;
				q[r][j]+=w;
				if(i==0) {
					ans[r]=6;
				} else {
					ans[r]=5;
				}
				if(p2(r)==r) return r;
			}
		}
		++h;
	}
	return 0;
}
int main() {
	int n;
	scanf("%d",&n);
	while(n--) {
		++tim;
		scanf("%d%d%d",&c[0],&c[1],&N);
		out(bfs());
		printf("\n");
	}
}
