#include <bits/stdc++.h>

using namespace std;

bool vis[1005][1005];

int A,B,c;

struct water
{
	int a,b;
	int step;
	int pre;
	int poc;
};

water q[1000005];
int front,rear;

void print(int idx)
{
	if(idx==0) return;
	print(q[idx].pre);
	cout<<q[idx].poc<<" ";
}

void bfs()
{
	water t;
	water nt;
	front=rear=0;
	t.a=0;
	t.b=0;
	t.step=0;
	vis[0][0]=1;
	q[rear++]=t;
	while(front<rear)
	{
		t=q[front++];
		if(t.b==c)
		{
			cout<<t.step<<" ";
			print(front-1);
			cout<<endl;
			break;
		}
		//fill A
		nt.a=A,nt.b=t.b,nt.step=t.step+1;
		nt.poc=1;
		nt.pre=front-1;
		if(!vis[nt.a][nt.b])
		{
			vis[nt.a][nt.b]=1;
			q[rear++]=nt;
		}
		//fill B 
		nt.a=t.a,nt.b=B,nt.step=t.step+1;
		nt.poc=2;
		if(!vis[nt.a][nt.b])
		{
			vis[nt.a][nt.b]=1;
			q[rear++]=nt;
		}
		//drop A
		nt.a=0,nt.b=t.b,nt.step=t.step+1;
		nt.poc=3;
		if(!vis[nt.a][nt.b])
		{
			vis[nt.a][nt.b]=1;
			q[rear++]=nt;
		}
		//drop B
		nt.a=t.a,nt.b=0,nt.step=t.step+1;
		nt.poc=4;
		if(!vis[nt.a][nt.b])
		{
			vis[nt.a][nt.b]=1;
			q[rear++]=nt;
		}
		//pour A B
		nt.poc=6;
		if(t.a+t.b>=B)
		{
			nt.a=t.a+t.b-B,nt.b=B,nt.step=t.step+1;
			if(!vis[nt.a][nt.b])
			{
				vis[nt.a][nt.b]=1;
				q[rear++]=nt;
			}
		}
		else
		{
			nt.a=0,nt.b=t.a+t.b,nt.step=t.step+1;
			if(!vis[nt.a][nt.b])
			{
				vis[nt.a][nt.b]=1;
				q[rear++]=nt;
			}
		}
		//pour B A
		nt.poc=5;
		if(t.a+t.b>=A)
		{
			nt.a=A,nt.b=t.a+t.b-A,nt.step=t.step+1;
			if(!vis[nt.a][nt.b])
			{
				vis[nt.a][nt.b]=1;
				q[rear++]=nt;
			}
		}
		else
		{
			nt.a=t.a+t.b,nt.b=0,nt.step=t.step+1;
			if(!vis[nt.a][nt.b])
			{
				vis[nt.a][nt.b]=1;
				q[rear++]=nt;
			}
		}
	}
}

int main()
{
	int num;
	cin>>num;
	while(num--)
	{
		memset(vis,0,sizeof(vis));
		memset(q,0,sizeof(q));
		cin>>A>>B>>c;
		bfs();
	}
	return 0;
}
