inline void dfs(int x,int y,int num) {
    int flag=0;
    for(int i=0; i<4; i++) {
        int xx=x+c[i],yy=y+b[i];
        if(a[xx][yy]>=a[x][y]) continue;
        if(f[xx][yy]) {
            ans=max(ans,f[xx][yy]+num);
            continue;
        }
        if(xx&&xx<=n&&yy&&yy<=m&&!p[xx][yy]) {
            p[xx][yy]=1;
            dfs(xx,yy,num+1);
            p[xx][yy]=0;
        }
    }
    if(!flag) {
        ans=max(ans,num);
        return;
    }
}
int main() {
