            ans=0;
            p[i][j]=1;
            dfs(i,j,1);
            p[i][j]=0;
            f[i][j]=ans;
            if(maxn<ans) maxn=ans;
        }
    }
    printf("%d\n",maxn);
    return 0;
}/*
