int search(int x,int y) {
    int i,t,tmp,nx,ny;
    if(f[x][y]>0) return f[x][y];
    t=1;
    for(i=1; i<=4; i++) {
        nx=x+dx[i];
        ny=y+dy[i];
        if((nx>=1)&&(nx<=r)&&(ny>=1)&&(ny<=c)&&(m[x][y]<m[nx][ny])) {
            tmp=search(nx,ny)+1;
            if(tmp>t) t=tmp;
        }
    }
    f[x][y]=t;
    return t;
}
