void dfs(int x,int y,int cnt)//x，y代表位置，cnt代表最优值
{
    if(cnt<=f[x][y]) return;
    f[x][y]=cnt;
    ans=max(ans,cnt);
    for(rint k=1;k<=4;k++)//向4个方向搜索
    {
        int p=x+dx[k],q=y+dy[k];
        if(s[p][q]<s[x][y]&&p&&p<=a&&q&&q<=b)
            dfs(p,q,cnt+1);
    }
}
