#include <cstdio>
#include <cstring>
#include <algorithm>

using namespace std;

int dx[]={0,0,1,-1};
int dy[]={1,-1,0,0};


int a[105][105];
int g[105][105];
int r,c;
int f(int x,int y){
	// 从 (x,y) 开始的最长路径长度 
	if(g[x][y]) return g[x][y];

	int ret = 1;
	for(int k=0;k<4;k++){
		int nx = x+dx[k];
		int ny = y+dy[k];
		if(nx>r || nx < 1 || ny > c || ny < 1) continue;
		if(a[nx][ny] < a[x][y]){
			ret = max(ret,f(nx,ny)+1);
		}
	}

	return g[x][y]=ret;
}

int main(){
