    int k=1;
    for(int i=1;i<=n;++i)
    {
        for(int j=1;j<=m;++j)
        {
            search(i,j,1);//搜索每个点的最大步数。
        }
    }
    for(int i=1;i<=n;++i)
    {
        for(int j=1;j<=m;++j)
        {
            if(k<b[i][j]) k=b[i][j];//找最大值
        }
    }
    cout<<k;
}
