![](https://cdn.luogu.com.cn/upload/pic/74277.png)
```cpp
#include <iostream>
using namespace std;
const int dx[4] = {-1, 0, 1, 0};
const int dy[4] = {0, 1, 0, -1};
const int N = 100;
int n, m, a[N][N];
int f[N][N];

void dfs(int x, int y) {
	f[x][y] = 1;

	for(int i = 0; i <= 3; i++) {
		int x2 = x + dx[i];
		int y2 = y + dy[i];

		if(x2 < 1 || x2 > n) continue; //越界

		if(y2 < 1 || y2 > m) continue;

		if(a[x2][y2] >= a[x][y]) continue;//比你高

		if(!f[x2][y2]) dfs(x2, y2);//没有走过

		f[x][y] = max(f[x][y], f[x2][y2] + 1); //状态转移，找最长的
	}
}

int main() {
	cin >> n >> m;

	for(int i = 1; i <= n; i++)
		for(int j = 1; j <= m; j++)
			cin >> a[i][j];

	int mx = 0;

	for(int i = 1; i <= n; i++)
		for(int j = 1; j <= m; j++) { //循环，防止走到死地
			if(!f[i][j]) dfs(i, j);//没走过

			mx = max(mx, f[i][j]);
		}

	cout << mx << endl;
	return 0;
}

