void dfs(int x,int y,int k);
int main(){
	scanf("%d%d",&r,&c);
	for(int i=0;i<=r+1;i++) a[i][0]=2^63-1;
	for(int i=0;i<=c+1;i++) a[0][i]=2^63-1;
	for(int i=0;i<=r+1;i++) a[i][c+1]=2^63-1;
	for(int i=0;i<=c+1;i++) a[r+1][i]=2^63-1;//硬核开墙
	for(int i=1;i<=r;i++){
		for(int j=1;j<=c;j++){
			scanf("%lld",&a[i][j]);
		}
	}
	memset(ch,false,sizeof(ch));
	for(int i=1;i<=r;i++){
		for(int j=1;j<=c;j++){
			dfs(i,j,1);
			m[i][j]=back;
			back=-1;
			ch[i][j]=true;
		}
	}
	for(int i=1;i<=r;i++){
		for(int j=1;j<=c;j++){
			ans=max(ans,m[i][j]);
		}
	}
	printf("%d",ans);
	return 0;
}
void dfs(int x,int y,int k){//记忆化搜索
	if(k>back&&a[x+1][y]>=a[x][y]&&a[x][y+1]>=a[x][y]&&a[x-1][y]>=a[x][y]&&a[x][y-1]>=a[x][y]){
		back=k;
		return;
	}//如果四周都去不了，比较一下就返回
	int x1,y1;
	for(int i=0;i<4;i++){
		x1=x+ex[i];
		y1=y+ey[i];
		if(a[x1][y1]<a[x][y]){//因为前面开了墙所以不要特判
			if(ch[x1][y1]){
				k+=m[x1][y1];
				if(back<k){
					back=k;
				}
				k-=m[x1][y1];
			}
			else{
				dfs(x1,y1,k+1);
			}
		}
	}
}


