#define RG register 
using namespace std;
inline int read()
{
    RG char ch=getchar();
    while(ch!='-'&&(ch>'9'||ch<'0')) ch=getchar();
    RG int h=0,t=1;
    if(ch=='-'){t=-1;ch=getchar();}
    while(ch>='0'&&ch<='9'){h=h*10+ch-48;ch=getchar();} 
