```cpp
#include <iostream>
#include <cstring>
#include <cstdio>
#include <cmath>
#include <algorithm>
#include <queue>
using namespace std;
const int dx[] = {0, 1, 0, -1};
const int dy[] = {1, 0, -1, 0};
const int N = 103;
struct Point {
    int x, y, step;
};
int h[N][N], f[N][N]; // f(i, j):从(i, j)开始能走的最长路 
int n, m, max_;
void bfs(const int &sx, const int &sy);
inline bool in_map(const int &x, const int &y);
int main()
{
    cin >> n >> m;
    // 周围加一圈INF，这样走到边界一定不满足h[i, j]>h[边界]
    // 于是乎自动停止，无需特判 
    for (int i = 0; i <= n + 1; ++i)
        h[0][i] = h[n + 1][i] = 105e7;
    for (int i = 0; i <= m + 1; ++i)
        h[i][0] = h[i][m + 1] = 105e7;
    for (int i = 1; i <= n; ++i)
        for (int j = 1; j <= m; ++j)
            cin >> h[i][j];
//    for (int i = 0; i <= n + 1; ++i) {
//        for (int j = 0; j <= m + 1; ++j)
//            cout << h[i][j] << " ";
//        cout << "\n";    
//    }
    for (int i = 1; i <= n; ++i)
        for (int j = 1; j <= n; ++j) {
            bfs(i, j);
            max_ = max(max_, f[i][j]);    
        }
    cout << max_ << "\n";
    return 0;
}
// BFS找从(sx, sy)开始的最长路 
void bfs(const int &sx, const int &sy)
{
    queue<Point> q;
    q.push({sx, sy, 1});
    int nx, ny, x, y, step;
    while (!q.empty()) {
        x = q.front().x;
        y = q.front().y;
        step = q.front().step;
        // 如果当前最长路长比已知更长则更新 
        f[sx][sy] = max(f[sx][sy], step);
        q.pop(); // !!! 
        for (int i = 0; i < 4; ++i) {
            nx = x + dx[i];
            ny = y + dy[i];
            if (in_map(nx, ny) && h[x][y] > h[nx][ny]) {
                // 记忆化，如果从(x, y)到(nx, ny)的距离step，
                // 因而不用再求(nx, ny)的最长路，f[nx, ny]以记录下了 
                if (f[nx][ny])
                    f[sx][sy] = max(f[sx][sy], step + f[nx][ny]);
                else
                    q.push({nx, ny, step + 1});
            }
        }
    }
}
inline bool in_map(const int &x, const int &y)
{ return x > 0 && x <= n && y > 0 && y <= m; }
