        for(int j=1;j<i;++j)if(mht(a[i],a[j])==1&&a[i].h>a[j].h)a[i].v=max(a[i].v,a[j].v+1);//比最长上升子序列唯一多的部分：曼哈顿距离为1才可选 
        ans=max(ans,a[i].v);//打擂台 
    }
}
