**int dfs(int x,int y)
    if(flag[x][y]) return flag[x][y]; 
    int num=0,temp=0;
    for(int k=0;k<4;k++){
        int tx,ty;
        tx= x + ne[k][0];
        ty= y + ne[k][1]; 
        if(tx<0||tx>r-1 ||ty<0 || ty>c-1) continue;   //边界 

        if(book[tx][ty]==0 && a[x][y]>a[tx][ty]){
            book[tx][ty]=1;
            num =dfs(tx,ty)+1;    
            if(num > temp){
                temp = num;            //保存最大值 
                flag[x][y]=num;        //保存路径 
            } 
            book[tx][ty]=0;
        }
    }
    return temp;
}
int main(){
    freopen("2.txt","r",stdin);
