Hash：

```cpp
#include<cstdio>
#include<cctype>
using namespace std;
const int N=300005,mod=1000007;
const int seed[12]={13,17,31,37,131,133,137,171,313,233,1313,2333};

int n,m,a,b,f[N],len,Hash[mod+10],val[mod+10];

inline int read()
{
    int now=0;bool f=0;char c=getchar();
    for(;!isdigit(c);c=getchar())
      if(c=='-') f=1;
    for(;isdigit(c);c=getchar())
      now=(now<<3)+(now<<1)+c-'0';
    return f?-now:now;
}

int GetHash(int x)
{
    long long res=1;
    int t=0;
    while(x)
      res=(res+(long long)x*seed[t++]%mod)%mod,x/=10;//减少冲突可能性
    return res;
}
void Insert(int x,int v)
{
    int now=GetHash(x);
//    printf("x:%d r:%d\n",x,now);
    while(Hash[now])//拉链法
    {
        ++now;
        if(now==mod) now=1;
    }
    Hash[now]=x;
    val[now]=v;//映射后的值
}
int Find(int x)
{
    int now=GetHash(x);
//    printf("x:%d r:%d\n",x,now);
    while(Hash[now]&&Hash[now]!=x)
    {
        ++now;
        if(now==mod) now=1;
    }
    if(Hash[now]==x) return val[now];//a的映射中存在x，返回其值
    return 0;//不存在该元素
}
int Search(int x)//同样的二分查找
{
    int l=1,r=len;
    while(l<r)
    {
        int m=l+r>>1;
        if(x>f[m])
          l=m+1;
        else
          r=m;
    }
    return l;
}

int main()
{
    n=read(),m=read();
    for(int i=1;i<=n;++i)
      a=read(),Insert(a,i);
    for(int i=1;i<=m;++i)
    {
        b=read();
        int x=Find(b);
//        printf("i:%d val:%d\n",GetHash(b),x);
        if(!x) continue;
        if(x>f[len])
            f[++len]=x;
        else
        {
            int p=Search(x);
            f[p]=x;
        }
    }
    printf("%d",len);
    return 0;
}
```

