int dy[8]={1,2,-2,-1,2,1,-1,-2};
struct Queue{
	int x,y;
}que[40010];//构建bfs的队列 
int check(int x,int y)
{
	if(x<1||x>n||y<1||y>m)
		return 0;
	return 1;
}//判断越界 
void bfs(int x,int y)
//x,y为初始坐标 
{
	int head=0,tail=1;//队列头尾指针 
	int sum=0;//步数清零 
	que[1].x=x;que[1].y=y; 
	//初始点入队 
	vis[x][y]=true;
	//将初始点标记 
	ans[x][y]=0;
	while(head<tail)//搜完了就退出 
	{
		head++;//头指针指向待扩张点 
		sum=ans[que[head].x][que[head].y]+1;
		//标记步数 
		for(int i=0;i<8;i++)
		{
			int nx=que[head].x+dx[i];
			int ny=que[head].y+dy[i];
			//扩展新点 
			if(check(nx,ny)&&!vis[nx][ny])
			//如果没走过 
			{
				tail++;
				que[tail].x=nx;que[tail].y=ny;
				//将两个点入队 
				vis[nx][ny]=true;
				//标记为搜过 
				ans[que[tail].x][que[tail].y]=sum;
				//标记步数 
			}
		}
	}
}
