        push(q[h].x + 1, q[h].y - 2);
        push(q[h].x + 2, q[h].y - 1);
        push(q[h].x + 2, q[h].y + 1);
        push(q[h].x + 1, q[h].y + 2);
        push(q[h].x - 1, q[h].y - 2);
        push(q[h].x - 2, q[h].y - 1);
        push(q[h].x - 2, q[h].y + 1);
