        if(x<1||x>n||y<1||y>m)continue; 
        if(sum+1<ans[xx][yy]||ans[xx][yy]==-1)
            dfs(xx,yy,sum+1);
    }//找八个儿子
}
int main()
{
    cin>>n>>m>>sx>>sy;
    memset(ans,-1,sizeof(ans));//初始化
    dfs(sx,sy,0);
    for (int i=1;i<=n;i++)
    {
        for(int j=1;j<=m;j++)
        {
            if(i==sx&&j==sy)cout<<0<<"    "; //当它本身就是起点时,输出0
            else if(ans[i][j]==0)printf("%-5d",-1);
            else printf("%-5d",ans[i][j]);
        }
        cout<<endl;
    }//完美的输出
    return 0;
}

