本题代码：

```cpp
#include<bits/stdc++.h>
using namespace std;
int n,m,bx,by,sum;
int mp[401][401];
int tx[8]={2,-2,2,-2,-1,1,-1,1},ty[8]={1,1,-1,-1,2,2,-2,-2};
bool v[401][401];
bool check(int a,int b){
    if(a>0&&b>0&&a<=n&&b<=m&&!v[a][b]) return 1;
    return 0;
}
void bfs(){
    queue<pair<int,int> >q;
    q.push(make_pair(bx,by));
    v[bx][by]=1;
    while(q.size()){
        int x=q.front().first,y=q.front().second;
        q.pop();
         for(int i=0;i<=7;i++){
         	int X=x+tx[i],Y=y+ty[i];
         	if(check(X,Y)){
         		v[X][Y]=1;
         		mp[X][Y]=mp[x][y]+1;
                 q.push(make_pair(X,Y));	
             }
         }
    }
}
int main(){
    scanf("%d%d%d%d",&n,&m,&bx,&by);
    memset(mp,0xff,sizeof(mp));
    mp[bx][by]=0;
    bfs();
    for(int i=1;i<=n;i++){
        for(int j=1;j<=m;j++){
            printf("%-5d",mp[i][j]);
        }
        printf("\n");
    }
    return 0;
}
