bool vis[maxn][maxn];
int n,m;
void bfs(int st,int ed){
    queue<point> q;
    q.push(point(st,ed,0));
    while(!q.empty()){
        point np=q.front();
        q.pop();
        vis[np.x][np.y]=true;
        np.step++;
        for(int i=0;i<8;++i){
            point nw=np;
            nw.x+=dx[i];
            nw.y+=dy[i];
            if(nw.x>n||nw.y>m||nw.x<1||nw.y<1||vis[nw.x][nw.y])
                continue;
            map[nw.x][nw.y]=nw.step;
            vis[nw.x][nw.y]=true;//之前没有这句 在这里卡了很久
            //这句必须有 
            //不同起步可能指向相同节点 必须操作后马上就标记避免重复入队 不要担心重复标记 标了没事 不标肯定死
            q.push(nw);
        }
    }
}
int main(){
    memset(map,-1,sizeof(map));
    cin>>n>>m>>st>>ed; 
    map[st][ed]=0; 
    bfs(st,ed);
    for(int i=1;i<=n;i++){
        for(int j=1;j<=m;j++){
            printf("%-5d",map[i][j]);//只会printf控制固定行输出。。。 
        }
        putchar(10); //10是换行符（"\n"）的ascii码 
    }
    return 0; 
}
