```cpp
#include <cstdio>
#include <algorithm>
#include <queue>
using namespace std;
queue <short> x; //定义横坐标队列
queue <short> y; //定义纵坐标队列
int n,m,a[405][405]; //存放步数
const int zh[8][2]={ //状态转移数组
    {1,2},
    {1,-2},
    {-1,2},
    {-1,-2},
    {2,1},
    {2,-1},
    {-2,1},
    {-2,-1}
};
void bfs(){
    int i,j,u,xx,yy;
    while(!(x.empty())){ //一直循环到队列为空
        xx=x.front();
        yy=y.front();
        x.pop(); //出队
        y.pop(); //出队
        for(u=0;u<8;u++){
            i=xx+zh[u][0]; 
            j=yy+zh[u][1];
            if(i>=1 && i<=n && j>=1 && j<=m && !(a[i][j])){ //判断是否越界或已经遍历过
                x.push(i); //入队
                y.push(j); //入队
                a[i][j]=a[xx][yy]+1; //步数为上一次加1
            }
        } //这个for循环用来把马跳到没有遍历到的地方去
    }
}
int main(){
    int p,q;
    scanf("%d %d %d %d",&n,&m,&p,&q);
    x.push(p); //横坐标入队
    y.push(q); //纵坐标入队
    a[p][q]=1; //步数从1开始，方便判断
    bfs(); //广搜
    for(p=1;p<=n;p++){
        for(q=1;q<=m;q++)
            printf("%-5d",a[p][q]?a[p][q]-1:-1); //输出步数时要减1
        printf("\n");
    }
    return 0;
}

