P q[1000010];

bool ma[10010][10010];

void input() {
	s(n);s(m);
	s(now_x);s(now_y);
	//起始路径 
	f(i,1,n) {
		f(j,1,m) {
			ans[i][j]=-1;
			//初始化 -1 好用又省劲 
		}
	}
}

void Bfs() {
	ans[now_x][now_y]=0;
	ma[now_x][now_y]=true; 
	//起点初始化
	int head=1,tail=1;
	q[head].fi=now_x;
	q[head].se=now_y;
	//队列初始化 
	while(head<=tail) {
		int x2=q[head].fi;
		int y2=q[head].se;
		head++; //出队 
		f(i,0,7) {
			//枚举方向 
