这道题是一道无脑BFS题，STL队列就行了。
```c
#include<bits/stdc++.h>
using namespace std;
struct node{
	int x,y,t;
}; 
int a[405][405],n,m,x,y;
bool vis[405][405];
int xx[8]={1,-1,2,-2,1,-1,2,-2},
	yy[8]={2,-2,1,-1,-2,2,-1,1};
inline void bfs(int x,int y,int ex,int ey){//对每个点找最短路径
	queue<node>q;
	q.push(node{x,y,0});
	while(!q.empty()){
		node u=q.front();
		q.pop();
		if(u.x==ex&&u.y==ey){
			a[ex][ey]=u.t;
			return;
		}
		for(int i=0;i<8;i++){
			int dx=xx[i]+u.x;
			int dy=yy[i]+u.y;
			if(vis[dx][dy]) continue;
			if(dx<1||dx>n||dy<1||dy>m) continue;
			vis[dx][dy]++;
			q.push(node{dx,dy,u.t+1});
		}
	}
}
int main(){
	ios::sync_with_stdio(0);
	cin.tie(0);
	cout.tie(0);
	cin>>n>>m>>x>>y;
	memset(a,-1,sizeof a);
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			memset(vis,0,sizeof vis);
			bfs(x,y,i,j);
		}
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			printf("%-5d",a[i][j]);
		}

	}
	return 0;
}
