priority_queue优化bfs
```cpp
#include<cmath>
#include<queue>
#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;
int n,m,sx,sy;
int dis[1000][1000];
struct edge
{
    int x;
    int y;
    int cost;
    bool friend operator < (edge a,edge b)
    {
        return a.cost>b.cost;
    }
};
int ask(int a,int b)
{
    return (a-1)*m+b;
}
int bfs()
{
    int vis[500][500]={0};
    int fx[8][2]={{1,2},{1,-2},{-1,-2},{-1,2},{2,1},{-2,1},{-2,-1},{2,-1}};
    memset(dis,-1,sizeof(dis));
    priority_queue<edge>q;
    edge now;
    now.x=sx;now.y=sy;now.cost=0;
    vis[sx][sy]=1;
    dis[sx][sy]=0;
    q.push(now);
    while (q.size())
    {
        now=q.top();q.pop();
        dis[now.x][now.y]=now.cost;
        for (int i=0;i<8;i++)
        {
            if (now.x+fx[i][1]>n||now.y+fx[i][0]>m||vis[now.x+fx[i][1]][now.y+fx[i][0]]||now.x+fx[i][1]<1||now.y+fx[i][0]<1) continue;
            vis[now.x+fx[i][1]][now.y+fx[i][0]]=1;
            edge t={now.x+fx[i][1],now.y+fx[i][0],now.cost+1};
            q.push(t); 
        }
    }
    return 0;
}
int main()
{
    scanf("%d %d %d %d",&n,&m,&sx,&sy);
    bfs();
    int ans=ask(sx,sy);
    for (int i=1;i<=n;i++){
        for (int j=1;j<=m;j++)
            printf("%-5d", dis[i][j]);
        printf("\n");
    }
    return 0;
}
