```cpp
#include <iostream>
#include <queue>
using namespace std;
int map[401][401], n, m, x, y;//map用来储存每个点的最少步数
struct point {
    int x, y;
    friend bool operator<(point a, point b) {//优先队列，步数少的考前
        return map[a.x][a.y] > map[b.x][b.y];
    }
};
priority_queue <point> q;
bool ok(int a,int b) {//判断一个点是否在界内并且没有访问过
    return a && b && a <= n && b <= m && map[a][b] == -1;
}
int main() {
    int i, j, k;
    int search[8][2] = {{-1, -2},
                        {-2, -1},
                        {-2, 1},
                        {-1, 2},
                        {1,  2},
                        {2,  1},
                        {2,  -1},
                        {1,  -2}};//每次可以走8个方向
    cin >> n >> m >> x >> y;
    for (i = 1; i <= n; i++)
        for (j = 1; j <= m; j++)
            map[i][j] = -1;//初始化为－1
    map[x][y] = 0;
    point a;
    a.x = x;
    a.y = y;
    q.push(a);//a代表初始点
    while (!q.empty()) {
        a = q.top();
        q.pop();
        for (i = 0; i < 8; i++)
            if (ok(a.x + search[i][0], a.y + search[i][1])) {//8个方向一个一个找
                map[a.x + search[i][0]][a.y + search[i][1]] = map[a.x][a.y] + 1;
                point f;
                f.x = a.x + search[i][0];
                f.y = a.y + search[i][1];
                q.push(f);//每找到一个界内的未访问的点，将其推入队列中
            }
    }
    for (i = 1; i <= n; i++) {
        for (j = 1; j < m; j++) {
            printf("%-5d", map[i][j]);//输出
        }
        printf("%d\n", map[i][j]);
    }
    return 0;
}

