# 完整代码

```cpp
#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
ll r, g, b, n, m, a[65], sz[65], vis[65], tot, f[25][25][25], mod;
inline ll qpow(ll x, ll y)
{
    ll res = 1;
    while (y)
    {
        if (y & 1)
            res = res * x % mod;
        x = x * x % mod;
        y >>= 1;
    }
    return res;
}
inline void getRing()
{
    memset(vis, 0, sizeof(vis));
    tot = 0;
    for (int i = 1; i <= n; i++)
        if (!vis[i])
        {
            int pos = i, len = 0;
            while (!vis[pos])
            {
                len++;
                vis[pos] = true;
                pos = a[pos];
            }
            sz[++tot] = len;
        }
}
inline ll calc()
{
    memset(f, 0, sizeof(f));
    f[0][0][0] = 1;
    for (int t = 1; t <= tot; t++)
        for (int i = r; i >= 0; i--)
            for (int j = g; j >= 0; j--)
                for (int k = b; k >= 0; k--)
                {
                    if (sz[t] <= i)
                        f[i][j][k] = (f[i][j][k] + f[i - sz[t]][j][k]) % mod;
                    if (sz[t] <= j)
                        f[i][j][k] = (f[i][j][k] + f[i][j - sz[t]][k]) % mod;
                    if (sz[t] <= k)
                        f[i][j][k] = (f[i][j][k] + f[i][j][k - sz[t]]) % mod;
                }
    return f[r][g][b];
}
int main()
{
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cin >> r >> g >> b >> m >> mod;
    n = r + g + b;
    for (int i = 1; i <= n; i++)
        a[i] = i;
    getRing();
    ll ans = calc();
    for (int i = 1; i <= m; i++)
    {
        for (int j = 1; j <= n; j++)
            cin >> a[j];
        getRing();
        ans = (ans + calc()) % mod;
    }
    cout << ans * qpow(m + 1, mod - 2) % mod;
    return 0;
}
