            if(A>=v[j]) dp[A][B][C]+=dp[A-v[j]][B][C];
            if(B>=v[j]) dp[A][B][C]+=dp[A][B-v[j]][C];
            if(C>=v[j]) dp[A][B][C]+=dp[A][B][C-v[j]];
            dp[A][B][C]%=p;
         }
      }
      ans+=dp[a][b][c];
      ans%=p;
   }
   printf("%lld",ans*getinv(m+1)%p);
