#include <cstdio>
#include <cstring>
#define N 105
using namespace std;
typedef long long LL;
int a,b,c,n,m,MOD,p[N];
LL f_pow(LL x,LL y) {
    LL tmp=1;
    while(y) {
        if(y&1) (tmp*=x)%=MOD;
        (x*=x)%=MOD;
        y>>=1;
    }
    return tmp;
}
int calc() {
    static bool k[N];
    static int s[N],top,f[N][N][N];
    memset(k,false,sizeof k);
    top=0;
    for(int i=1;i<=n;i++) {
        if(k[i]) continue;
