        while(!k[tmp]) k[tmp]=true, cnt++, tmp=p[tmp];
        s[top++]=cnt;
    }
    memset(f,0,sizeof f);
    f[0][0][0]=1;
    while(top--)
        for(int i=a;~i;i--)
            for(int j=b;~j;j--)
                for(int t=c;~t;t--) {
                    if(i>=s[top]) (f[i][j][t]+=f[i-s[top]][j][t])%=MOD;
                    if(j>=s[top]) (f[i][j][t]+=f[i][j-s[top]][t])%=MOD;
                    if(t>=s[top]) (f[i][j][t]+=f[i][j][t-s[top]])%=MOD;
                }
    return f[a][b][c];
}
int main() {
