```cpp
#include <iostream>
using namespace std;
int s[1100],top=0;
void push(int k)//入栈 
{
    s[top]=k;top++;
}
void pop(char k)//判断加减乘除 
{
    if(k=='+'){
        s[top-2]=s[top-1]+s[top-2];
    }
    if(k=='-'){
        s[top-2]=s[top-2]-s[top-1];
    }
    if(k=='*'){
        s[top-2]=s[top-1]*s[top-2];
    }
    if(k=='/'){
        s[top-2]=s[top-2]/s[top-1];
    }top--;//栈顶要-- 
}
int main()
{
