int BFS_IAA_(int x,int y)
{
    int head=1,tail=0,tx,ty,count=0;
    map[x][y]='0';
    qx[head]=x;
    qy[head]=y;
    do
    {
        count++;
        tail++;
        tx=qx[tail];
        ty=qy[tail];
        for (int i=0;i<4;i++)
            {
