```cpp
#include <iostream>
#include <cstdio>
#include <cstring>
#define re register int
using namespace std;

int n,m,ans,map[101][101],ty[4][2]={{1,0},{0,1},{-1,0},{0,-1}};

inline void dfs(int x,int y)
{
	for(re i=0;i<=3;i++)
	{
		re xz=x+ty[i][0];
		re yz=y+ty[i][1];
		if(xz>=1&&xz<=n&&yz>=1&&yz<=m&&map[xz][yz]!=0)
		{
			map[xz][yz]=0;
			dfs(xz,yz);
		}
	}
}

int main()
{
	std::scanf("%d%d",&n,&m);

	for(re i=1;i<=n;i++)
		for(re j=1;j<=m;j++)
		{
			char temp;   
			std::cin>>temp;
			map[i][j]=temp-48; 
		}


