void dfs(int x,int y)
{
	if(x>n||y<1||y>m||x<1||a[x][y]==0) return;
	a[x][y]=0;//标记一下，免得等下再搜回来死循环
	dfs(x+1,y);//四个方向
	dfs(x,y+1);//分别判联通
	dfs(x-1,y);
	dfs(x,y-1);
}
