int n,m;bool u[101][101]={0};char a[101][101];
void dfs(int i,int j,int fw){//方位
    if(i==0||j==0||a[i][j]=='0'||u[i][j]||a[i][j]==0){
        u[i][j]=true;
        return ;
    }
    //cout<<i<<' '<<j<<' '<<endl;
    if(!(i==0||j==0||a[i][j]=='0'||u[i][j]||a[i][j]==0)) {
        u[i][j]=true;
        if(fw==0){
            dfs(i,j+1,3);
            dfs(i,j-1,1);
            dfs(i+1,j,2);
            dfs(i-1,j,4);
        }
        if(fw==1){
            dfs(i,j-1,1);
            dfs(i+1,j,2);
            dfs(i-1,j,4);
        }
        if(fw==2){
            dfs(i,j+1,3);
            dfs(i+1,j,2);
            dfs(i,j-1,1);
        }
        if(fw==3){
            dfs(i,j+1,3);
            dfs(i+1,j,2);
            dfs(i-1,j,4);
        }
        if(fw==4){
            dfs(i,j+1,3);
            dfs(i,j-1,1);
            dfs(i-1,j,4);
        }
    }
}
int main(){
