```cpp
#include<bits/stdc++.h>
using namespace std;
int m,n,dx[4]={1,0,-1,0},dy[4]={0,1,0,-1},ans=0,x,y,t,w;
//上面花括号的是定义方向，搜索。 
int dl[5000][3];
char spore;
bool bz[111][111];
void bfs(int q,int p)//bfs，宽搜。 
{
    t=1;
    w=1;
    bz[q][p]=false;
    dl[1][1]=q;
    dl[1][2]=p;
    while(t<=w)//当头小于尾的时候才开始循环，避免鬼畜。。 
    {
        for(int s=0;s<4;s++)//四个方向搜索。 
        {
            x=dl[t][1]+dx[s];
            y=dl[t][2]+dy[s];
            if(x>=0&&y>=0&&x<m&&y<n&&bz[x][y])
            {
                w++;
                bz[x][y]=false;
                dl[w][1]=x;
                dl[w][2]=y;
            }
        }
        t++;
    }
}
int main()
{
    cin>>m>>n;
    for(int i=0;i<m;i++)
    for(int j=0;j<n;j++)
    {
